var relativeDir = '../..';
var loader = require('./lib/Loader.js').Loader;
var maker = require('./lib/Maker.js').Maker;
var orms = require(relativeDir + '/config/orm.js').orms;

exports.init = function() {
    // load I Framework core
    loader.loadCore();

    // create base model files in memory
    maker.loadOrms(orms);

    // write model file to disk
    maker.createFiles(orms, './model');

    // load model classes in memory
    loader.loadMemoryModelClasses(maker.getClasses());

    // load disk classes
    loader.load(relativeDir);
};

