!function () {
    var Exception = function Exception(code, err) {
        if (err) console.log(err);
        
        console.log('Error', code, ':', I.ExceptionCodes[code]);
        Error.call(this, code);
        this.message = code;
    };

    Exception.prototype = new Error();
    Exception.prototype.constructor = Exception;

    I.Util.require('Exception', '', Exception);
}();
